// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import GlobalStyle from './styles/global';
import Navbar from './components/Navbar';
import Form from './components/Form';
import Grid from './components/Grid';
import { useState } from 'react';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';



const App = () => {
  const [users, setUsers] = useState([]);
  const [onEdit, setOnEdit] = useState(null);

  const getUsers = async () => {
    try {
      const res = await axios.get('http://localhost:8800/clientes');
      setUsers(res.data.sort((a, b) => (a.nome > b.nome ? 1 : -1)));
    } catch (error) {
      toast.error(error.message);
    }
  };

  return (
    <Router>
      <GlobalStyle />
      <Navbar />
      <Container>
        <Routes>
          <Route path="/" element={<Title>Bem-vindo ao CRUD</Title>} />
          <Route path="/clientes" element={<><Form onEdit={onEdit} setOnEdit={setOnEdit} getUsers={getUsers} /><Grid users={users} setUsers={setUsers} setOnEdit={setOnEdit} /></>} />
          <Route path="/autores" element={<Authors />} />
          <Route path="/livros" element={<Books />} />
          {/* Adicione mais rotas conforme necess√°rio */}
        </Routes>
        <ToastContainer autoClose={3000} position="bottom-left" />
      </Container>
    </Router>
  );
};

export default App;
