import React from 'react';
import { Routes, Route } from 'react-router-dom';
import GlobalStyle from "./styles/global";
import styled from 'styled-components';
import Form from "./paginas/Form";
import Grid from "./paginas/Grid";
import AutoresPage from "./paginas/AutoresPage";
import LivrosPage from "./paginas/LivrosPaginas";
import Menu from "./components/menu";
import { useEffect, useState } from 'react';
import { toast, ToastContainer } from 'react-toastify';
import "react-toastify/dist/ReactToastify.css";
import axios from 'axios';

const Container = styled.div`
  width: 100%;
  max-width: 800px;
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
`;

function App() {
  const [users, setUsers] = useState([]);
  const [onEdit, setOnEdit] = useState(null);

  const getUsers = async () => {
    try {
      const res = await axios.get("http://localhost:8800/clientes");
      setUsers(res.data.sort((a, b) => (a.nome > b.nome ? 1 : -1)));
    } catch (error) {
      toast.error(error.message || "An error occurred");
    }
  };

  useEffect(() => {
    getUsers();
  }, [setUsers]);

  return (
    <>
      <Menu />
      <Container>
        <Routes>
          <Route path="/" element={<Grid users={users} setUsers={setUsers} setOnEdit={setOnEdit} />} />
          <Route path="/form" element={<Form onEdit={onEdit} setOnEdit={setOnEdit} getUsers={getUsers} />} />
          <Route path="/autores" element={<AutoresPage />} />
          <Route path="/livros" element={<LivrosPage />} />
        </Routes>
      </Container>
      <ToastContainer autoClose={3000} position="bottom-left" />
      <GlobalStyle />
    </>
  );
}

export default App;
